import { Component } from '@angular/core';
import { UrlService } from './services/url.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'url-shortener-ui';

  originalUrl = '';
  alias = '';
  shortUrl = '';

  constructor(private urlService: UrlService) {}

    generate() {
    this.urlService.create(this.originalUrl, this.alias)
      .subscribe(result => {
        this.shortUrl = `https://localhost:7210/${result.code}`;
      });
    }
}
<div style="max-width: 500px; margin: 40px auto; font-family: Arial;">
  <h2>URL Shortener</h2>

  <div>
    <label>URL original</label><br>
    <input type="text" [(ngModel)]="originalUrl" style="width:100%">
  </div>

  <br>

  <div>
    <label>Alias (opcional)</label><br>
    <input type="text" [(ngModel)]="alias" style="width:100%">
  </div>

  <br>

  <button (click)="generate()">Gerar</button>

  <div *ngIf="shortUrl" style="margin-top:20px">
    <p>URL encurtada:</p>
    <a [href]="shortUrl" target="_blank">{{ shortUrl }}</a>
  </div>
</div>
